import{_ as r,C as s,o,c as d,k as e,a,H as n,w as t,Q as l}from"./chunks/framework.6730c137.js";const j=JSON.parse('{"title":"Missing Abstraction","description":"","frontmatter":{},"headers":[],"relativePath":"girish/abstraction/missing/index.md","filePath":"girish/abstraction/missing/index.md"}'),k={name:"girish/abstraction/missing/index.md"},g=l('<h1 id="missing-abstraction" tabindex="-1">Missing Abstraction <a class="header-anchor" href="#missing-abstraction" aria-label="Permalink to &quot;Missing Abstraction&quot;">​</a></h1><p>Smell ini terjadi ketika:</p><ul><li>Ada sekumpulan data yang dibiarkan primitif (mirip Data Clumps Martin Fowler).</li><li>Ada encoded string yang dibiarkan primitif (mirip Primitive Obsession Martin Fowler).</li></ul><p>Bila smell ini dibiarkan, maka bisa menyebabkan:</p><ul><li>Detail code tersebar dan terekspos di banyak tempat. Menyebabkan tanggungjawab di class yang bersangkutan menjadi tidak jelas (melanggar SRP).</li><li>Terjadi tight-coupling antar class yang memegang data mentah yang tersebar.</li></ul><h2 id="penyebab-smell" tabindex="-1">Penyebab Smell <a class="header-anchor" href="#penyebab-smell" aria-label="Permalink to &quot;Penyebab Smell&quot;">​</a></h2><ul><li><strong>Inadequate design analysis</strong>: software developer tidak teliti ketika proses desain. Biasa terjadi karena developer dikejar deadline sehingga terburu-buru dalam melakukan code. Yang penting cepat selesai.</li><li><strong>Lack of refactoring</strong>: ketika program belum terlalu besar, mungkin saja menggunakan data mentah masih cukup rapi. Seiring berjalannya waktu, ketika ada fitur baru, ternyata data mentah dipakai lagi di tempat lain, dan developer belum sadar bahwa sudah saatnya melakukan refactoring.</li><li><strong>Misguided focus on minor performance gains</strong>: developer berusaha meningkatkan performance aplikasi, misal dari O(3N+5) menjadi O(2N+3). Ketika performance yang ditingkatkan relatif sedikit, sebaiknya kita lebih fokus kepada code yang rapi. Demi efisiensi bisnis, maintainable code jauh lebih penting ketimbang meningkatkan sedikit performance. Server dan compiler sekarang makin canggih dan murah.</li></ul><h2 id="contoh" tabindex="-1">Contoh <a class="header-anchor" href="#contoh" aria-label="Permalink to &quot;Contoh&quot;">​</a></h2><h3 id="masalah" tabindex="-1">Masalah <a class="header-anchor" href="#masalah" aria-label="Permalink to &quot;Masalah&quot;">​</a></h3><p>Terdapat data-data yang berkaitan dengan ISBN (International Standard Book Number).</p><p>ISBN adalah kode unik yang dibeli oleh penerbit buku untuk diletakkan di buku terbitannya. Kode unik ini terdiri dari: registration group, registrant, publication dan check digit.</p><p>ISBN yang dipakai sekarang ini memiliki 2 bentuk. ISBN-10 (mengandung 10 digit) dan ISBN-13 (mengandung 13 digit). ISBN-10 bisa dikonversi menjadi ISBN-13, dan sebaliknya. Kedua bentuk ini juga punya algoritma tersendiri untuk mengecek apakah kode tersebut valid atau tidak. Detail algoritma konversi dan pengecekan dapat dilihat di <a href="https://en.wikipedia.org/wiki/International_Standard_Book_Number" target="_blank" rel="noreferrer">Wikipedia</a>.</p>',12),u=e("h3",{id:"penyelesaian",tabindex:"-1"},[a("Penyelesaian "),e("a",{class:"header-anchor",href:"#penyelesaian","aria-label":'Permalink to "Penyelesaian"'},"​")],-1),m=e("a",{href:"after/ISBNFactory.java"},"ISBNFactory",-1),h=e("h2",{id:"when-to-ignore",tabindex:"-1"},[a("When to Ignore "),e("a",{class:"header-anchor",href:"#when-to-ignore","aria-label":'Permalink to "When to Ignore"'},"​")],-1),b=e("p",null,"Smell ini tidak perlu di-refactor bila malah menyebabkan over-engineering.",-1);function c(p,f,_,y,S,B){const i=s("github-url");return o(),d("div",null,[g,e("p",null,[a("Perhatikan "),n(i,{to:"before/Book.java"},{default:t(()=>[a("Book.java")]),_:1}),a(" pada package before. Terdapat String ISBN yang memiliki behavior pengecekan dan konversi. Behavior ini tentunya bukan merupakan tanggungjawab class Book. String ISBN seharusnya tidak disimpan secara mentah di dalam class ini.")]),u,e("p",null,[a("ISBN diekstrak menjadi hirarki baru. "),n(i,{to:"after/ISBN.java"},{default:t(()=>[a("ISBN.java")]),_:1}),a(" sebagai parent-nya dan diturunkan menjadi "),n(i,{to:"after/ISBN10.java"},{default:t(()=>[a("ISBN10.java")]),_:1}),a(" dan "),n(i,{to:"after/ISBN13.java"},{default:t(()=>[a("ISBN13.java")]),_:1}),a(". Agar switch statement lebih rapi, dibuat "),m,a(" untuk digunakan oleh class "),n(i,{to:"after/Book.java"},{default:t(()=>[a("Book")]),_:1}),a(".")]),h,b])}const v=r(k,[["render",c]]);export{j as __pageData,v as default};
